const INITIAL_STATE={
    currentUser:null
}
///if state is undefined, initial_state is passsed through. null isconsidered a value
const userReducer = (state=INITIAL_STATE,action) =>{
    switch(action.type){
        case 'SET_CURRENT_USER':
            return{
                ...state,
                currentUser:action.payload
            };

        default : return state;
    }
}
export default userReducer;
